<?phpuse \ApiTester;use Faker\Factory as Faker;class MotionResourceCest{    public function _before(ApiTester $I)    {        $I->loginAsAdmin();    }    public function _after(ApiTester $I)    {    }    // tests    protected $endpoint = '/api/motion';    public $id;    public function getAllMotions(ApiTester $I){        $I->sendGET($this->endpoint);        $I->seeResponseCodeIs(200);        $I->seeResponseIsJson();        $I->seeResponseContainsJson(['id' => 1, 'title' => 'A brand new motion']);    }    public function getSingleMotion(ApiTester $I){        $I->sendGET($this->endpoint."/1");        $I->seeResponseCodeIs(200);        $I->seeResponseIsJson();        $I->seeResponseContainsJson(['id' => 1, 'title' => 'A brand new motion']);    }    public function createMotion(ApiTester $I){        $I->sendPOST($this->endpoint, array            (            'title' => 'A second brand new motion',             'summary' => 'A more or less bad summary',            'text' => 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Ut mollis lacinia dui quis tristique. Etiam tellus orci, auctor vitae turpis id, iaculis pellentesque mi. ',            'active' => 1            ));        $I->seeResponseCodeIs(200);        $I->seeResponseIsJson();        $id = $I->grabDataFromResponseByJsonPath('id');        $I->seeResponseContainsJson(['user_id' => 1, 'title' => 'A second brand new motion']);        $I->seeRecord('motions', ['id' => $id, 'title' => 'A second brand new motion']);    }    public function updateMotion(ApiTester $I){        $I->sendPATCH($this->endpoint, array            (            'id' => $id,            'title' => 'A second brand new motion, updated'            ));        $I->seeResponseCodeIs(200);        $I->seeResponseIsJson();        $I->seeResponseContainsJson(['user_id' => 1, 'title' => 'A second brand new motion, updated']);        $I->seeRecord('motions', ['title' => 'A second brand new motion, updated']);        $I->dontSeeRecord('motions', ['title' => 'A second brand new motion']);    }    public function deleteMotion(ApiTester $I, $id){        $I->sendDELETE($this->endpoint);        $I->seeResponseCodeIs(200);        $I->dontSeeRecord('motions', ['id' => $id]);    }}